import XMLData from './languages_all.xml';


function App() {


  // Read XML file and store it as a string
  const [XMLtext, setXMLtext] = useState("");
  axios.get(XMLData, {
    "Content-Type": "application/xml; charset=utf-8"
  })
    .then((response) => {
      setXMLtext(response.data)
    });

  // Get Spanish translation from string
  getTranslation()
  console.log(XMLtext);

  return (
    <div className="App">
      <header className="App-header">
        <button>Click me to see the Spanish translation!</button>
        <span>Spaanse text</span>
      </header>
    </div>
  );
}

function Welcome(props) {
  return <h1>Hello, {props.name}</h1>;
}


export default App;
