import './App.css';
import axios from 'axios';
// import XMLParser from 'react-xml-parser';


import React, { useState } from 'react';
import XMLData from './languages_all.xml';


function App() {


  // Read XML file and store it as a string
  const [XMLtext, setXMLtext] = useState("");
  const [clicked, setClicked] = useState(false);


  axios.get(XMLData, {
    "Content-Type": "application/xml; charset=utf-8"
  })
    .then((response) => {
      setXMLtext(response.data)
    });


  // // const xmlStr = '<a id="a"><b id="b">hey!</b></a>';
  // const parser = new DOMParser();
  // const dom = parser.parseFromString(XMLtext, "application/xml");
  // // print the name of the root element or error message
  // console.log(dom.getElementsByTagName('endTour'));



  // Get Spanish translation from string
  getTranslation(XMLtext, "endTour", "sp")
  // console.log(XMLtext);

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={() => setClicked(true)}>Click me to see the Spanish translation!</button>
        <input type="text" name="name" />
        {clicked &&
          <span>Spaanse text</span>}
      </header>
    </div>
  );
}

function getTranslation(XMLtext, element, language) {
  // console.log(element);
  return "Fin le";
}


export default App;
